mapper.select:
SELECT * FROM ( SELECT A.* , ROWNUM RNUM FROM ( SELECT R.BOARD_ID , R.RECRUIT_DT , R.TITLE , R.CONTENT , R.EMAIL B_EMAIL , R.HIT , R.WRT_DT , R.MDFY_DT , M.EMAIL M_EMAIL , M.NAME FROM RECRUITBOARD R , RECRUITMEMBER M WHERE R.EMAIL = M.EMAIL
AND TITLE LIKE '%' ||#{searchKeyword} || '%'
ORDER BY BOARD_ID DESC ) A WHERE ROWNUM <= #{endRow} ) WHERE RNUM >= #{startRow}

mapper.insert:
